INSERT INTO wines (id, name, year, country, region, wine_color, variety, winery, rating, price, style)
SELECT
    nextval('wines_id') AS id, -- Dynamically generate the `id` using a sequence
    wine->>'name' AS name,
    CASE
    WHEN wine->>'year' ~ '^\d{4}$' THEN (wine->>'year')::INT  -- Only cast if the value is a valid 4-digit year
    ELSE NULL  -- Set NULL if the year is invalid
END AS year,
    wine->>'country' AS country,
    wine->>'region' AS region,
    (wine->>'wine_type_id')::INT AS wine_color,
    wine->>'most_used_grapes' AS variety,
    wine->>'winery' AS winery,
    (wine->>'rating')::FLOAT AS rating,
    (wine->>'price')::FLOAT AS price,
    wine->>'style' AS style
FROM json_data;